**Web端实现（Vue 3 + Composition API）：**

html
<template>
  <transition name="dialog-fade">
    <div 
      v-if="show"
      class="dialog-overlay"
      @click.self="close"
      @keydown.esc="close"
    >
      <div 
        class="dialog-content"
        role="dialog"
        aria-labelledby="dialog-title"
        aria-describedby="dialog-desc"
      >
        <h2 id="dialog-title">删除商品？</h2>
        <p id="dialog-desc">删除后将从购物车中移除</p>

        <div class="dialog-actions">
          <button 
            class="btn-cancel"
            @click="close"
            ref="cancelBtn"
          >
            取消
          </button>
          <button 
            class="btn-confirm"
            @click="confirm"
            ref="confirmBtn"
          >
            删除
          </button>
        </div>
      </div>
    </div>
  </transition>
</template>

<script setup>
import { ref, watch, nextTick, onMounted } from 'vue'

const props = defineProps({
  show: Boolean
})

const emit = defineEmits(['confirm', 'close'])

const cancelBtn = ref(null)
const confirmBtn = ref(null)

// 焦点管理
watch(() => props.show, async (newVal) => {
  if (newVal) {
    await nextTick()
    // 保存当前焦点元素
    window._previousFocus = document.activeElement
    // 焦点移至确认按钮
    confirmBtn.value?.focus()
  } else {
    // 恢复焦点
    window._previousFocus?.focus?.()
  }
})

// 键盘导航
const handleKeydown = (e) => {
  if (!props.show) return

  if (e.key === 'Tab') {
    e.preventDefault()
    const focusable = [cancelBtn.value, confirmBtn.value]
    const currentIndex = focusable.indexOf(document.activeElement)
    const nextIndex = e.shiftKey ? 
      (currentIndex - 1 + focusable.length) % focusable.length :
      (currentIndex + 1) % focusable.length
    focusable[nextIndex]?.focus()
  }
}

onMounted(() => {
  document.addEventListener('keydown', handleKeydown)
})
</script>

