#!/bin/bash

# 获取所有命名空间的Pod资源请求与限制汇总
kubectl get pods --all-namespaces -o json | jq -r '
# 定义单位转换函数
def to_milli_cpu(cpu):
    if cpu == null then 0
    elif cpu | test("[0-9]+m") then cpu | sub("m";"") | tonumber
    else cpu | tonumber * 1000
    end;

def to_mebibytes(mem):
    if mem == null then 0
    elif mem | test("[0-9]+Mi") then mem | sub("Mi";"") | tonumber
    elif mem | test("[0-9]+Gi") then mem | sub("Gi";"") | tonumber * 1024
    elif mem | test("[0-9]+Ki") then mem | sub("Ki";"") | tonumber / 1024
    else mem | sub("[^0-9.]";"") | tonumber
    end;

# 处理每个Pod
[.items[] | {
    namespace: .metadata.namespace,
    pod: .metadata.name,
    containers: [.spec.containers[] | {
        cpu_req: (.resources.requests.cpu // "0"),
        mem_req: (.resources.requests.memory // "0"),
        cpu_lim: (.resources.limits.cpu // "0"),
        mem_lim: (.resources.limits.memory // "0")
    }]
}] |

# 按命名空间分组并汇总
group_by(.namespace) | map({
    namespace: .[0].namespace,
    cpu_req_m: ([.[].containers[].cpu_req | to_milli_cpu(.)] | add),
    mem_req_mi: ([.[].containers[].mem_req | to_mebibytes(.)] | add),
    cpu_lim_m: ([.[].containers[].cpu_lim | to_milli_cpu(.)] | add),
    mem_lim_mi: ([.[].containers[].mem_lim | to_mebibytes(.)] | add)
}) |

# 格式化输出
(map(.) | 
    (["Namespace", "CPU_Req(m)", "Mem_Req(Mi)", "CPU_Lim(m)", "Mem_Lim(Mi)"], 
    (.[] | [.namespace, .cpu_req_m, .mem_req_mi, .cpu_lim_m, .mem_lim_mi])) |
    @tsv
)
' | column -t -s $'\t'

