#!/bin/bash

echo "正在收集集群资源使用情况..."
echo "=========================================="

# 单次获取数据并处理
kubectl get pods --all-namespaces -o json | jq -r '
# 单位转换函数
def to_millicore(cpu):
    if cpu == null then 0
    elif cpu | type == "number" then cpu * 1000
    elif cpu | test("^[0-9]+m$") then (cpu | sub("m"; "") | tonumber)
    elif cpu | test("^[0-9.]+$") then (cpu | tonumber * 1000)
    else 0 end;

def to_mebibyte(mem):
    if mem == null then 0
    elif mem | test("^[0-9]+Mi$") then (mem | sub("Mi"; "") | tonumber)
    elif mem | test("^[0-9]+Gi$") then (mem | sub("Gi"; "") | tonumber * 1024)
    elif mem | test("^[0-9]+Ki$") then (mem | sub("Ki"; "") | tonumber / 1024)
    elif mem | test("^[0-9]+M$") then (mem | sub("M"; "") | tonumber / 1.049)  # MB to MiB
    elif mem | test("^[0-9]+G$") then (mem | sub("G"; "") | tonumber * 953.674)  # GB to MiB
    elif mem | test("^[0-9]+K$") then (mem | sub("K"; "") | tonumber / 1073.74)  # KB to MiB
    else (mem | sub("[^0-9.]"; "") | tonumber? // 0) end;

# 处理所有Pod数据
.data = [.items[] | {
    ns: .metadata.namespace,
    containers: [.spec.containers[] | {
        cpu_req: (.resources.requests.cpu // "0"),
        mem_req: (.resources.requests.memory // "0"),
        cpu_lim: (.resources.limits.cpu // "0"),
        mem_lim: (.resources.limits.memory // "0")
    }]
}];

# 按命名空间汇总
.summary = (.data | group_by(.ns) | map({
    namespace: .[0].ns,
    cpu_req_m: ([.[].containers[].cpu_req | to_millicore(.)] | add),
    mem_req_mi: ([.[].containers[].mem_req | to_mebibyte(.)] | add),
    cpu_lim_m: ([.[].containers[].cpu_lim | to_millicore(.)] | add),
    mem_lim_mi: ([.[].containers[].mem_lim | to_mebibyte(.)] | add)
}));

# 输出完整汇总表
"📊 资源请求与限制汇总表（所有命名空间）\n",
(["命名空间", "CPU请求(m)", "内存请求(Mi)", "CPU限制(m)", "内存限制(Mi)"], 
 .summary[] | [.namespace, .cpu_req_m, .mem_req_mi, .cpu_lim_m, .mem_lim_mi]) | @tsv,

"\n\n📈 按CPU请求总量排序：\n",
(["命名空间", "CPU请求(m)", "内存请求(Mi)"],
 (.summary | sort_by(-.cpu_req_m)[] | [.namespace, .cpu_req_m, .mem_req_mi])) | @tsv,

"\n\n📈 按内存请求总量排序：\n",
(["命名空间", "内存请求(Mi)", "CPU请求(m)"],
 (.summary | sort_by(-.mem_req_mi)[] | [.namespace, .mem_req_mi, .cpu_req_m])) | @tsv,

"\n\n📈 按CPU限制总量排序：\n",
(["命名空间", "CPU限制(m)", "内存限制(Mi)"],
 (.summary | sort_by(-.cpu_lim_m)[] | [.namespace, .cpu_lim_m, .mem_lim_mi])) | @tsv,

"\n\n📈 按内存限制总量排序：\n",
(["命名空间", "内存限制(Mi)", "CPU限制(m)"],
 (.summary | sort_by(-.mem_lim_mi)[] | [.namespace, .mem_lim_mi, .cpu_lim_m])) | @tsv
' | while IFS=$'\t

