#!/bin/bash

# 获取集群所有Pod资源使用情况
kubectl get pods --all-namespaces -o json | jq -r '
# 单位转换函数 - 精确转换
def to_millicore(cpu):
    if cpu == null then 0
    elif cpu | type == "number" then cpu * 1000
    elif cpu == "0" then 0
    elif cpu | test("^[0-9]+n$") then (cpu | sub("n"; "") | tonumber) / 1000000
    elif cpu | test("^[0-9]+u$") then (cpu | sub("u"; "") | tonumber) / 1000
    elif cpu | test("^[0-9]+m$") then (cpu | sub("m"; "") | tonumber)
    elif cpu | test("^[0-9.]+$") then (cpu | tonumber * 1000)
    else 0 end;

def to_mebibyte(mem):
    if mem == null then 0
    elif mem == "0" then 0
    # 二进制单位 (IEC标准)
    elif mem | test("^[0-9]+Ki$") then (mem | sub("Ki"; "") | tonumber) / 1024
    elif mem | test("^[0-9]+Mi$") then (mem | sub("Mi"; "") | tonumber)
    elif mem | test("^[0-9]+Gi$") then (mem | sub("Gi"; "") | tonumber) * 1024
    elif mem | test("^[0-9]+Ti$") then (mem | sub("Ti"; "") | tonumber) * 1024 * 1024
    elif mem | test("^[0-9]+Pi$") then (mem | sub("Pi"; "") | tonumber) * 1024 * 1024 * 1024
    # 十进制单位 (SI标准)
    elif mem | test("^[0-9]+K$") then (mem | sub("K"; "") | tonumber) * 1000 / (1024*1024)
    elif mem | test("^[0-9]+M$") then (mem | sub("M"; "") | tonumber) * 1000 * 1000 / (1024*1024)
    elif mem | test("^[0-9]+G$") then (mem | sub("G"; "") | tonumber) * 1000 * 1000 * 1000 / (1024*1024)
    elif mem | test("^[0-9]+T$") then (mem | sub("T"; "") | tonumber) * 1000 * 1000 * 1000 * 1000 / (1024*1024)
    elif mem | test("^[0-9]+P$") then (mem | sub("P"; "") | tonumber) * 1000 * 1000 * 1000 * 1000 * 1000 / (1024*1024)
    # 无单位 - 假设为字节
    elif mem | test("^[0-9]+$") then (mem | tonumber) / (1024*1024)
    else 0 end;

