      progressDots: '底部动态进度点'
    }
  }
};

// 稳定性验证逻辑
function validateStability() {
  return new Promise((resolve) => {
    const stabilityCheck = {
      requiredStableFrames: 3, // 连续3帧无变化
      lastViewportHeight: null,
      stableFrameCount: 0,

      checkFrame: () => {
        const currentHeight = window.visualViewport.height;
        if (currentHeight === this.lastViewportHeight) {
          this.stableFrameCount++;
        } else {
          this.stableFrameCount = 0;
        }
        this.lastViewportHeight = currentHeight;

        if (this.stableFrameCount >= this.requiredStableFrames) {
          resolve(true);
        } else {
          requestAnimationFrame(this.checkFrame.bind(this));
        }
      }
    };
    stabilityCheck.checkFrame();
  });
}


